import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { 
  Card,
  ResourceList,
  Stack,
  TextStyle,
  Thumbnail, 
  Tabs
} from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';
import Country_data from "../components/Country_data";


class ResourceListWithProducts extends React.Component {

  constructor(props){
    super(props);
    this.handleOnSelect = this.handleOnSelect.bind(this);
    this.state = {
      list: [],
      selected: 0 
    }
    console.log("1")
  }
  static contextType = Context;

  componentDidMount() {
    this.getList();
    console.log("2")
  }

  getList = () => {
    //fetch('/api/getList')
    //fetch('https://ebb450c1.ngrok.io')
    fetch('http://localhost:5000/api/tabs')
    .then(res => res.json())
    .then(list => this.setState({ list }))
    console.log("3")
  }

  /*
  getCountryData = (country_code) => {
    //fetch('/api/getList')
    //fetch('https://ebb450c1.ngrok.io')
    fetch('http://localhost:5000/api/tabs')
    .then(res => res.json())
    .then(list => this.setState({ list }))
    console.log("3")
  }*/


  render() {
    const { list, selected } = this.state;
    console.log("List is ", list)
    const app = this.context;
    const tabs = list
    //console.log('ids are ',store.get('ids') )
    return (
      <Tabs tabs={tabs} selected={selected} onSelect={(selected2) => this.handleOnSelect({selected2})} fitted={true}>
      <p>in tab {selected}</p>
      <Country_data/>

      </Tabs>
    );
  }
  handleOnSelect() {
    this.setState({selected: selected2});
  }

}

 export default ResourceListWithProducts;
