import '../style/style.css'
import { AppProvider, Page, Card, DataTable, Button, ResourcePicker, Layout } from '@shopify/polaris';
import '@shopify/polaris/styles.css';
import translations from '@shopify/polaris/locales/en.json';
import {FaLock } from 'react-icons/fa';
import store from 'store-js';
import Modal from '../components/Modal_Currency'
import React, {useState, useCallback, OptionList, setSelected} from 'react';
let request_bid_ask = require('/Users/tatianatian/Desktop/Startup/shopify_front_back_end/trade/trades/src/request_bid_ask.js')
import {HorizontalBar, Line, Doughnut, Bar} from 'react-chartjs-2';



const rows = [['GBP', 1.3, 1.2, 100, '15']]



var lineChartData = {
    labels: ['04-19', '04-22', '04-25', '04-28', '05-01', '05-04', '05-07', '05-10', '05-13'],
    datasets: [{
      type: "bar",
      label: "real",
      fill: false,
      lineTension: 0,
      backgroundColor: "rgb(1, 2, 58)",
      borderColor: "rgb(1, 2, 58)",
      borderCapStyle: 'butt',
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: 'miter',
      pointBorderColor: "rgb(1, 2, 58)",
      pointBackgroundColor: "rgb(1, 2, 58)",
      pointBorderWidth: 1,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: "rgb(1, 2, 58)",
      pointHoverBorderColor: "rgb(1, 2, 58)",
      pointHoverBorderWidth: 2,
      pointRadius: 4,
      pointHitRadius: 10,
      // notice the gap in the data and the spanGaps: false
      data: [1, 2, 3, 4, 3, 2, 4, 3, 1],
      spanGaps: false,
      },
      {
        type: "line",
        label: "real",
        fill: false,
        lineTension: 0,
        backgroundColor: "rgb(1, 2, 58)",
        borderColor: "rgb(1, 2, 58)",
        borderCapStyle: 'butt',
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: 'miter',
        pointBorderColor: "rgb(1, 2, 58)",
        pointBackgroundColor: "rgb(1, 2, 58)",
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgb(1, 2, 58)",
        pointHoverBorderColor: "rgb(1, 2, 58)",
        pointHoverBorderWidth: 2,
        pointRadius: 4,
        pointHitRadius: 10,
        // notice the gap in the data and the spanGaps: false
        data: [7, 7.1005, 7.0906, 7.0879, 7.1372, 7.1542, 7.1257, 7.1673, 7.1562],
        spanGaps: false,
        }

    ]
  };

  var lineChartOptions = {
    maintainAspectRatio: false,
    tooltips:{
      enabled:true,
    },
    legend: {
      display: false,
    },
    scales: {
              yAxes: [{
                  ticks: {
                      beginAtZero:false
                  },
                  scaleLabel: {
                       display: false,
                       labelString: 'USD/CNH',
                       fontSize: 20 
                    }
              }],
              xAxes: [{
                gridLines: {
                  color: "rgba(0, 0, 0, 0)",
              }
              },],            
          },
          layout: {
            padding: {
                left: 0,
                right: 50,
                top: 0,
                bottom: 0
            }
        }   
  };



  const barChartData = {
    labels: ['China - Monthly CPI', 'US - Monthly CPI', 'China - Quarterly GDP',
             'US - Aaa Corporate Bonds', 'US - S&P 500'],
    datasets: [
      {
        backgroundColor: ["#ffa41b", '#005082','#ff5733','#708160','#00a8cc'],
        borderColor: 'rgba(0, 0, 0, 0)',
        borderWidth: 2,
        data: [0.65, 0.50, 0.47, 0.39, 0.37]
      }
    ],
  };

  const barChartOptions = {

    legend: {
              display: false,
            },

    scales: {
              xAxes: [{
                ticks: {
                  min: 0,
                  max: 1,
                  callback: function(value) {
                      return value
                  }
                },
                scaleLabel: {
                  display: true,
                  labelString: "Correlation Coefficient (Absolute Value)"
                }
              },],
              yAxes: [{
                barPercentage: 0.3,
                gridLines: {
                  color: "rgba(0, 0, 0, 0)",
              }
              },],
            },

    };

    const doughnutChartData = {
        labels: ['USD/CNH will trend lower', 'USD/CNH will trend higher'],
        datasets: [
          {
            backgroundColor: ["#FF6D2D","#45853C"],
            borderColor: 'rgba(0, 0, 0, 0)',
            borderWidth: 2,
            data: [28, 72],

          }
        ],
      };

      const doughnutChartOptions = {
        maintainAspectRatio: false,
        cutoutPercentage:70,
        tooltips:{
          enabled:false,
        },
        legend: {
                  display: true,
                },
        pieceLabel: {
          render: 'value' + '%',
          fontColor: "white",
        }
        };


// Function

class dashboard extends React.Component {
    // Initialize the state
    constructor(props){
      super(props);
      this.state = {
        list: [],
        open_currency: false,
        rows:[],
        active: true,
        selected: []
      }
      console.log("1")
    }
    // Fetch the list on first mount
    componentDidMount() {
      this.getList();
      console.log("2")
    }

    // Retrieves the list of items from the Express app
    getList = () => {
      //fetch('/api/getList')
      //fetch('https://ebb450c1.ngrok.io')
      fetch('http://localhost:5000/api/getList')
      .then(res => res.json())
      .then(list => this.setState({ list }))
      console.log("3")
    }  


  render() {
    const { list, open_currency } = this.state;

    return (
    <Layout>
        <Layout.Section oneHalf>
            <p>Sales and product foreign prices</p>
            <Bar 
            height="320vh"
            data={lineChartData}
            options={lineChartOptions}
            />

        </Layout.Section>
        <Layout.Section oneHalf>
            <p>Products whose sales sensitive to foreign prices</p>
            <HorizontalBar 
                data={barChartData}
                options={barChartOptions}
            />
            <Card>
            <Doughnut
                height="20vh"
                data={doughnutChartData}
                options={doughnutChartOptions}
            />
            </Card>
        </Layout.Section>



    </Layout>


    );




  }


}



 export default dashboard;
