import React from 'react';
import {Card, Layout} from '@shopify/polaris';
import {HorizontalBar, Doughnut, Bar} from 'react-chartjs-2';
import store from 'store-js';
const getPearsonCorrelation = require('../functions/calc_correlation')
const string_to_list = require('../functions/string_to_list')




export default function Country_data() {  

    const date_string = localStorage.getItem('date')
    const price_string = localStorage.getItem('price')
    const exchange_rate_string = localStorage.getItem('exchange_rate')
    const current_level_string = localStorage.getItem('current_level')
    const correlation = localStorage.getItem('correlation')

    var date = []
    var price = []
    var exchange_rate = []
    var current_level = []

    const min_bar = 0
    const max_bar = 0
    const min_line = 0
    const max_line = 0


    if (date_string.includes(',')){
        date = string_to_list(localStorage.getItem('date'),'')
        price = string_to_list(store.get('price'),'float')
        exchange_rate = string_to_list(store.get('exchange_rate'),'float')
        current_level = string_to_list(store.get('current_level'),'float')

        min_bar = Math.min(price)*0.8
        max_bar = (Math.max(price)-min_bar)*2+min_bar

        max_line = Math.max(exchange_rate)*1.1
        min_line = max_line - (max_line - Math.min(exchange_rate))*2


    } else {
        date = [date_string]
        price = [price_string]
        exchange_rate = [exchange_rate_string]
        current_level = string_to_list(store.get('current_level'),'float')
    }




    //const level = store.get('level') 
    var r = parseInt(Math.min(Math.abs(parseFloat(correlation)), 1) * 100)
    //r = parseInt(Math.min(Math.abs(getPearsonCorrelation([34,37,24,67,42 ],[1.23,1.22, 1.23, 1.24, 1.25])), 1) * 100)
    const doughnut = [r, 100-r]


    var leve_num1 = [Math.max(current_level[0]*100-2,0), Math.max(current_level[1]*100-2,0), Math.max(current_level[2]*100-2,0), Math.max(current_level[3]*100-2,0)]
    console.log("level-num1 is ", leve_num1)
    var leve_num3 = [98-leve_num1[0],98-leve_num1[1],98-leve_num1[2],98-leve_num1[3]]
    console.log("level-num3 is ", leve_num3)
    const leve_num2 = [2, 2, 2, 2]



    console.log("date is ", date)
    console.log("price is ", price)
    console.log("exchange_rate is ", exchange_rate)
    console.log("current_level is ", current_level)
    console.log("correlation is ", correlation)


    var color = ''
    var text = ''
    if (r < 30){
        color = '#a2d6c4'
        text = 'light'
    } else if (r < 60){
        color = '#ffb367'
        text = 'moderate'
    } else {
        color = '#FF2D00'
        text = 'heavy'
    }



    const doughnutChartData = {
      labels: [text+': '+r],
      datasets: [
        {
          //backgroundColor: ["#FF6D2D","#45853C"],
          backgroundColor: [color,"#EEEEEE"],
          borderColor: 'rgba(0, 0, 0, 0)',
          borderWidth: 2,
          data: doughnut,

        }
      ],
    };

    const doughnutChartOptions = {
      circumference: 1* Math.PI,
      rotation: Math.PI,
      maintainAspectRatio: true,
      cutoutPercentage:80,
      tooltips:{
        enabled:false,
      },
      legend: {
                display: true,
                position: 'bottom',
                align:'center',
                labels:{
                    fontSize: 25,
                    boxWidth: 0,
                    fontColor: '#01023A'
                }
              },
      pieceLabel: {
        render: 'value' + '%',
        fontColor: "white",
      }
      };

    var lineChartData = {
      //labels: ["2020-05-08", "2020-05-09", "2020-05-10"],
      labels: date,
      //lables: [price[0],price[1],price[2]],
      datasets: [{
        yAxisID: 'A',
        type: "bar",
        label: "sales in store currency",
        fill: false,
        lineTension: 0,
        backgroundColor: "rgb(1, 2, 58)",
        borderColor: "rgb(1, 2, 58)",
        borderCapStyle: 'butt',
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: 'miter',
        pointBorderColor: "rgb(1, 2, 58)",
        pointBackgroundColor: "rgb(1, 2, 58)",
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgb(1, 2, 58)",
        pointHoverBorderColor: "rgb(1, 2, 58)",
        pointHoverBorderWidth: 2,
        pointRadius: 4,
        pointHitRadius: 10,
        // notice the gap in the data and the spanGaps: false
        //data: [89.54, 96.8, 246.51],
        data: price,
        //data: [store.get('price')],
        spanGaps: false,
        barPercentage: 0.3
        },
        {
          yAxisID: 'B',
          type: "line",
          label: "product price",
          fill: false,
          lineTension: 0,
          backgroundColor: "rgb(1, 2, 58)",
          borderColor: "rgb(1, 2, 58)",
          borderCapStyle: 'butt',
          borderDash: [],
          borderDashOffset: 0.0,
          borderJoinStyle: 'miter',
          pointBorderColor: "rgb(1, 2, 58)",
          pointBackgroundColor: "rgb(1, 2, 58)",
          pointBorderWidth: 1,
          pointHoverRadius: 5,
          pointHoverBackgroundColor: "rgb(1, 2, 58)",
          pointHoverBorderColor: "rgb(1, 2, 58)",
          pointHoverBorderWidth: 2,
          pointRadius: 4,
          pointHitRadius: 10,
          // notice the gap in the data and the spanGaps: false
          //data: [0.8072950291, 0.8072950291, 0.8072950291],
          data: exchange_rate,
          //data: [store.get('exchange_rate')],
          spanGaps: false,
          }

      ]
      };

    var lineChartOptions = {
      maintainAspectRatio: true,
      tooltips:{
        enabled:true,
      },
      legend: {
        display: false,
      },
      scales: {
                yAxes:
                [{
                    id: 'A',
                    type: 'linear',
                    position: 'left',
                    gridLines: {
                        color: "rgba(0, 0, 0, 0)",
                    },


                  }, {
                    id: 'B',
                    type: 'linear',
                    position: 'right',
                    gridLines: {
                        color: "rgba(0, 0, 0, 0)",
                    },


                  }],
                xAxes: [{
                  gridLines: {
                    color: "rgba(0, 0, 0, 0)",
                }
                },],            
            },
            layout: {
              padding: {
                  left: 0,
                  right: 50,
                  top: 0,
                  bottom: 0
              }
          }   
    };


    const barChartData = {
      labels: ['China - Monthly CPI', 'US - Monthly CPI', 'China - Quarterly GDP',
                'US - Aaa Corporate Bonds', 'US - S&P 500'],
      datasets: [
        {
          backgroundColor: ["#ffa41b", '#005082','#ff5733','#708160','#00a8cc'],
          borderColor: 'rgba(0, 0, 0, 0)',
          borderWidth: 2,
          data: [0.65, 0.50, 0.47, 0.39, 0.37]
        }
      ],
    };

    const barChartOptions = {

      legend: {
                display: false,
              },

      scales: {
                xAxes: [{
                  ticks: {
                    min: 0,
                    max: 1,
                    callback: function(value) {
                        return value
                    }
                  },
                  scaleLabel: {
                    display: true,
                    labelString: "Correlation Coefficient (Absolute Value)"
                  }
                },],
                yAxes: [{
                  barPercentage: 0.3,
                  gridLines: {
                    color: "rgba(0, 0, 0, 0)",
                }
                },],
              },
    };

    const rangeChartData = {
        labels: ['past 3 days', 'past 1 week', 'past 1 month', 'past 3 months'],
        datasets: [
        {
        label: 'range',
        backgroundColor: 'rgb(1, 2, 58)',
        stack: '3',
        data: leve_num1,
        },
        {
        backgroundColor: '#32DC8B',
        stack: '3',
        data: leve_num2,
        },
        {
          label: 'range2',
          backgroundColor: 'rgb(1, 2, 58)',
          stack: '3',
          data: leve_num3,
          },
        ],
      };

      const rangeChartOptions = {
        maintainAspectRatio: false,
        tooltips:{
          enabled:false,
        },
        legend: {
                  display: false,
                },
        scales: {
                  xAxes: [{
                    stacked: true,
                    gridLines: {
                      color: "rgba(0, 0, 0, 0)",
                    },
                    ticks: {
                      min: 0,
                      max: 100,
                      callback: function(value) {
                          return value + "%"
                      }
                    }
                  },],
                  yAxes: [{
                    stacked: true,
                    barPercentage: 0.4,
                    gridLines: {
                      color: "rgba(0, 0, 0, 0)",
                  }

                  },],
                },
                layout: {
                  padding: {
                      left: 0,
                      right: 30,
                      top: 0,
                      bottom: 0
                  }
              }  
        };





    return (


            <Layout>
            <Layout.Section oneHalf>
            <Card>


                <p>Price Change Magnitude in USD</p>
                <Doughnut
                    height="100vh"
                    data={doughnutChartData}
                    options={doughnutChartOptions}
                />
                <p>Price vs Sales in USD</p>
                <Bar 
                  height="200vh"
                  data={lineChartData}
                  options={lineChartOptions}
                />
                </Card>
            </Layout.Section>
            <Layout.Section oneHalf>
            <Card>
                <p>The current price level in USD</p>
                <HorizontalBar 
                    height="220vh"
                    data={rangeChartData}
                    options={rangeChartOptions}
                    />
            </Card>
            <Card>
                <h5>Product Ranking</h5>
                <p>Rankings of products on how easily sales are affected by price change</p>
                <HorizontalBar 
                        height="170vh"
                        data={barChartData}
                        options={barChartOptions}
                    />
            </Card>
            </Layout.Section>
          </Layout>

    );
  }
