var jsregression = require('js-regression');

const linear_regression = (sales, exchange_rate)=>{
    var [data, myMap] = reformat_data(sales, exchange_rate)
    console.log("data is ", data)
    var regression = new jsregression.LinearRegression();
    console.log("here")
    var model = regression.fit(data);

    let myMap2 = new Map();
    myMap2 = myMap

    // sort exchange rate
    exchange_rate.sort(function(a, b){return a-b});
    var regression_fx = []
    var regression_sales = []
    var regression_predicted_sales = []

    for (j=0;j<exchange_rate.length;j++){
        regression_fx.push(exchange_rate[j])
        regression_sales.push(myMap2.get(exchange_rate[j]))
        //console.log("[exchange_rate[i]] is ", exchange_rate[j])
        regression_predicted_sales.push(regression.transform([exchange_rate[j]]))
    }
    return [regression_fx, regression_sales, regression_predicted_sales]
}

function reformat_data(sales, exchange_rate){
    const data = []
    let myMap = new Map()
    for (i=0;i<exchange_rate.length;i++){
        var round_fx = Math.round(1000*exchange_rate[i])/1000;
        if (myMap.has(round_fx)){
            var sales_list = myMap.get(round_fx)
            sales_list.push(sales[i])
            myMap.set(round_fx,sales_list)
        }
        myMap.set(round_fx,[sales[i]])
    }
    for (let [k, v] of myMap) {
        let sum = v.reduce((previous, current) => current += previous);
        let avg = sum / v.length;
        data.push([k, avg])
    }

    return [data, myMap]
}

module.exports = linear_regression; 

