import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { 
  Card,
  ResourceList,
  Stack,
  TextStyle,
  Thumbnail, 
  Tabs
} from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';


class ResourceListWithProducts extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      list: [],
      open_currency: false,
      selected: 0 
    }
    console.log("1")
  }
  static contextType = Context;

  componentDidMount() {
    this.getList();
    console.log("2")
  }

  getList = () => {
    //fetch('/api/getList')
    //fetch('https://ebb450c1.ngrok.io')
    fetch('http://localhost:5000/api/tabs')
    .then(res => res.json())
    .then(list => this.setState({ list }))
    console.log("3")
  }


  render() {
    const { list, open_currency } = this.state;
    console.log("List is ", list)
    const app = this.context;
    const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();
    const tabs = list
    //console.log('ids are ',store.get('ids') )
    return (
      <Tabs tabs={tabs} selected={this.state.selected} onSelect={(selected) => this.setState({selected})} fitted={true}>

      </Tabs>
    );
  }

}

 export default ResourceListWithProducts;
