import '../style/style.css'
import { AppProvider, Page, Card, DataTable, Button, ResourcePicker } from '@shopify/polaris';
import '@shopify/polaris/styles.css';
import translations from '@shopify/polaris/locales/en.json';
import {FaLock } from 'react-icons/fa';
import store from 'store-js';
import ResourceListWithProducts from '../components/ResourceList';
import {useState, useCallback, OptionList, setSelected} from "react";


// Data
const rows = [
  ['GBP', '1.23', 1.24, '10,000 GBP', '$15.82'],
  ['EUR', '1.10', 1.09, '5,000 EUR', '$7.62'],
  ['CAD', '1.07', 1.08, '2,000 CAD', '$11.34'],
];

// Function

function EmbeddedAppResourcePickerExample() {
  const [active, setActive] = useState(false);

  const handleResourcePickerClose = useCallback(() => setActive(false), []);

  const handleSelection = useCallback(
    ({selection}) => {
      console.log('Selected products: ', selection);
      handleResourcePickerClose();
    },
    [handleResourcePickerClose],
  );

  return (
    <AppProvider>
      <ResourcePicker
        resourceType="Product"
        open={active}
        onSelection={handleSelection}
        onCancel={handleResourcePickerClose}
      />
    </AppProvider>
  );
}


function OptionListExample() {
  const [selected, setSelected] = useState([]);

  return (
    <Card>
      <OptionList
        title="Inventory Location"
        onChange={setSelected}
        options={[
          {value: 'byward_market', label: 'Byward Market'},
          {value: 'centretown', label: 'Centretown'},
          {value: 'hintonburg', label: 'Hintonburg'},
          {value: 'westboro', label: 'Westboro'},
          {value: 'downtown', label: 'Downtown'},
        ]}
        selected={selected}
      />
    </Card>
  );
}




class productsPrice extends React.Component {
    // Initialize the state
    constructor(props){
      super(props);
      this.state = {
        list: [],
        open_currency: false,
      }
      console.log("1")
    }
    // Fetch the list on first mount
    componentDidMount() {
      this.getList();
      console.log("2")
    }

    // Retrieves the list of items from the Express app
    getList = () => {
      //fetch('/api/getList')
      //fetch('https://ebb450c1.ngrok.io')
      fetch('http://localhost:5000/api/getList')
      .then(res => res.json())
      .then(list => this.setState({ list }))
      console.log("3")
    }  



  render() {
    const { list } = this.state;
    return (
      <div className="main_background_image">
      <h1>List of Items23</h1>
      {/* Check to see if any items are found*/}
      {list.length ? (
        <div>
          {/* Render the list of items */}
          {list.map((item) => {
            return(
              <div>
                {item}
              </div>
            );
          })}
        </div>
      ) : (
        <div>
          <h2>No List Items Found</h2>
        </div>
      )
    }



      <Page title="Gauranteed Exchange Rate">
      <OptionListExample/>
            <Card>
              <DataTable
                columnContentTypes={[
                  'text',
                  'numeric',
                  'numeric',
                  'numeric',
                  'numeric',
                ]}
                headings={[
                  "Buyer's currency",
                  'Gauranteed Exchange Rate',
                  'Real-time Exchange Rate',
                  'Gauranteed Amount',
                  'Extra Revenue',
                ]}
                rows={rows}
                totals={['', '', '', '', '$34.78']}
              />
            </Card>
          </Page>
          <Button onClick={(this.handleClick)}><FaLock/> Add Currency</Button>

          <Card>
      <OptionList
        title="Inventory Location"
        onChange={setSelected}
        options={[
          {value: 'byward_market', label: 'Byward Market'},
          {value: 'centretown', label: 'Centretown'},
          {value: 'hintonburg', label: 'Hintonburg'},
          {value: 'westboro', label: 'Westboro'},
          {value: 'downtown', label: 'Downtown'},
        ]}
        selected={selected}
      />
    </Card>

    </div>


    );
  }

  handleClick = () => {
    //alert('Button clicked!')
    //console.log("arrived handleClick");
    this.setState({ open_currency: true })

  };



}



 export default productsPrice;
