import {
  Button,
  Card,
  Form,
  FormLayout,
  Layout,
  Page,
  SettingToggle,
  Stack,
  TextField,
  TextStyle,
} from '@shopify/polaris';

class AnnotatedLayout extends React.Component {
  render() {
    return (
      <Layout.AnnotatedSection
      title="Check exchange rates"
      description="Select your currency and amount to insure, we will give you our exchange rate rate instantly."
      >
        <Card sectioned>
          <Form>
            <FormLayout>
              <TextField

                label="Currency"
                type="currency"
              />
              <TextField

                label="Amount"
                type="amount"
                helpText={
                  <span>
                    Weâ€™ll use this currency and amount to check the exchange rate we can insure you.
                  </span>
                }
              />
              <Stack distribution="trailing">
                <Button submit>
                  Check
                </Button>
              </Stack>
            </FormLayout>
          </Form>
        </Card>
      </Layout.AnnotatedSection>
    );
  }

  handleSubmit = () => {
    this.setState({
      discount: this.state.discount,
    });
    console.log('submission', this.state);
  };
}

export default AnnotatedLayout;
