import '../style/style.css'
import { AppProvider, Page, Card, DataTable, Button, ResourcePicker } from '@shopify/polaris';
import '@shopify/polaris/styles.css';
import translations from '@shopify/polaris/locales/en.json';
import {FaLock } from 'react-icons/fa';
import store from 'store-js';
import Modal from '../components/Modal_Currency'
import React, {useState, useCallback, OptionList, setSelected} from 'react';
let request_bid_ask = require('/Users/tatianatian/Desktop/Startup/shopify_front_back_end/trade/trades/src/request_bid_ask.js')
import {HorizontalBar, Line, Doughnut, Bar} from 'react-chartjs-2';



const rows = [['GBP', 1.3, 1.2, 100, '15']]



var lineChartData = {
    labels: ['04-19', '04-22', '04-25', '04-28', '05-01', '05-04', '05-07', '05-10', '05-13'],
    datasets: [{
      type: "bar",
      label: "real",
      fill: false,
      lineTension: 0,
      backgroundColor: "rgb(1, 2, 58)",
      borderColor: "rgb(1, 2, 58)",
      borderCapStyle: 'butt',
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: 'miter',
      pointBorderColor: "rgb(1, 2, 58)",
      pointBackgroundColor: "rgb(1, 2, 58)",
      pointBorderWidth: 1,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: "rgb(1, 2, 58)",
      pointHoverBorderColor: "rgb(1, 2, 58)",
      pointHoverBorderWidth: 2,
      pointRadius: 4,
      pointHitRadius: 10,
      // notice the gap in the data and the spanGaps: false
      data: [1, 2, 3, 4, 3, 2, 4, 3, 1],
      spanGaps: false,
      },
      {
        type: "line",
        label: "real",
        fill: false,
        lineTension: 0,
        backgroundColor: "rgb(1, 2, 58)",
        borderColor: "rgb(1, 2, 58)",
        borderCapStyle: 'butt',
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: 'miter',
        pointBorderColor: "rgb(1, 2, 58)",
        pointBackgroundColor: "rgb(1, 2, 58)",
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgb(1, 2, 58)",
        pointHoverBorderColor: "rgb(1, 2, 58)",
        pointHoverBorderWidth: 2,
        pointRadius: 4,
        pointHitRadius: 10,
        // notice the gap in the data and the spanGaps: false
        data: [7, 7.1005, 7.0906, 7.0879, 7.1372, 7.1542, 7.1257, 7.1673, 7.1562],
        spanGaps: false,
        }

    ]
  };

  var lineChartOptions = {
    maintainAspectRatio: false,
    tooltips:{
      enabled:false,
    },
    legend: {
      display: false,
    },
    scales: {
              yAxes: [{
                  ticks: {
                      beginAtZero:false
                  },
                  scaleLabel: {
                       display: false,
                       labelString: 'USD/CNH',
                       fontSize: 20 
                    }
              }],
              xAxes: [{
                gridLines: {
                  color: "rgba(0, 0, 0, 0)",
              }
              },],            
          },
          layout: {
            padding: {
                left: 0,
                right: 50,
                top: 0,
                bottom: 0
            }
        }   
  };



  const rangeChartData = {
    labels: ['past 3 days', 'past 1 week', 'past 1 month', 'past 3 months'],
    datasets: [
    {
    label: 'range',
    backgroundColor: 'rgb(1, 2, 58)',
    stack: '3',
    data: [98, 98, 98, 90],
    },
    {
    backgroundColor: '#32DC8B',
    stack: '3',
    data: [2, 2, 2, 2],
    },
    {
      label: 'range2',
      backgroundColor: 'rgb(1, 2, 58)',
      stack: '3',
      data: [0, 0, 0, 8],
      },
    ],
  };

  const rangeChartOptions = {
    maintainAspectRatio: false,
    tooltips:{
      enabled:false,
    },
    legend: {
              display: false,
            },
    scales: {
              xAxes: [{
                stacked: true,
                gridLines: {
                  color: "rgba(0, 0, 0, 0)",
                },
                ticks: {
                  min: 0,
                  max: 100,
                  callback: function(value) {
                      return value + "%"
                  }
                }
              },],
              yAxes: [{
                stacked: true,
                barPercentage: 0.4,
                gridLines: {
                  color: "rgba(0, 0, 0, 0)",
              }

              },],
            },
            layout: {
              padding: {
                  left: 0,
                  right: 30,
                  top: 0,
                  bottom: 0
              }
          }  
    };


// Function

class dashboard extends React.Component {
    // Initialize the state
    constructor(props){
      super(props);
      this.state = {
        list: [],
        open_currency: false,
        rows:[],
        active: true,
        selected: []
      }
      console.log("1")
    }
    // Fetch the list on first mount
    componentDidMount() {
      this.getList();
      console.log("2")
    }

    // Retrieves the list of items from the Express app
    getList = () => {
      //fetch('/api/getList')
      //fetch('https://ebb450c1.ngrok.io')
      fetch('http://localhost:5000/api/getList')
      .then(res => res.json())
      .then(list => this.setState({ list }))
      console.log("3")
    }  


  render() {
    const { list, open_currency } = this.state;

    return (
      <div className="main_background_image">
      <h1>List of Items</h1>
      {/* Check to see if any items are found*/}
      {list.length ? (
        <div>
          {/* Render the list of items */}
          {list.map((item) => {
            return(
              <div>
                {item}
              </div>
            );
          })}
        </div>
      ) : (
        <div>
          <h2>No List Items Found</h2>
        </div>
      )
    }
        <div>
            <Bar 
            height="320vh"
            data={lineChartData}
            options={lineChartOptions}
            />
        </div>    
        <div>
            <HorizontalBar 
            height="320vh"
            data={rangeChartData}
            options={rangeChartOptions}
            />
        </div>
    </div>


    );




  }


}



 export default dashboard;
