import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { 
  Card,
  ResourceList,
  Stack,
  TextStyle,
  Thumbnail, 
} from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';

/*
const GET_PRODUCTS_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        handle
        descriptionHtml
        id
        images(first: 1) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
        variants(first: 1) {
          edges {
            node {
              price
              id
            }
          }
        }
      }
    }
  }
`;*/

const GET_PRODUCTS_BY_ID = gql`
  query getProducts() {
      orders(query: "created_at:>=2019-01-01", first: 249) {
        edges {
          node {
            id
            customerLocale
            customer {
              addresses {
                countryCode
                country
                countryCodeV2
              }
            }
            totalPriceSet {
              presentmentMoney {
                amount
                currencyCode
              }
              shopMoney {
                amount
                currencyCode
              }
            }
            createdAt
          }
        }
      }
    }
`;


class ResourceListWithProducts extends React.Component {
  static contextType = Context;
  render() {
    const app = this.context;
    const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();
    console.log('ids are ',store.get('ids') )
    return (
      //<Query query={GET_PRODUCTS_BY_ID} variables={{ ids: store.get('ids') }}>
      <Query query={GET_PRODUCTS_BY_ID}>


        {({ data, loading, error }) => {
          if (loading) return <div>Loadingâ€¦</div>;
          if (error) return <div>{error.message}</div>;
          console.log(data);
          return (
            <Card>
              <ResourceList
                showHeader
               //resourceName={{ singular: 'Product', plural: 'Products' }}
                items={data.nodes}
                renderItem={item => {
                  const price = item.variants.edges[0].node.totalPriceSet.shopMoney.amount;
                  const quote = 1.2;
                  return (
                    <ResourceList.Item
                      //id={item.id}
                      //media={media}
                      //accessibilityLabel={`View details for ${item.title}`}
                      onClick={() => {
                        store.set('item', item);
                        //redirectToProduct();
                      }}
                    >
                      <p>here is the product </p>
                      <Stack>
                        <Stack.Item fill>
                          <h3>
                            <TextStyle variation="strong">
                            {item.title}
                            </TextStyle>
                          </h3>
                        </Stack.Item>
                        <Stack.Item>
                          <p>${price}</p>
                          <p>{quote}</p>
                        </Stack.Item>
                        <Stack.Item>
                          <p>Expires on {twoWeeksFromNow} </p>
                        </Stack.Item>
                      </Stack>
                    </ResourceList.Item>
                  );
                }}
              />
            </Card>
          );
        }}
      </Query>    
    );
  }
}

 export default ResourceListWithProducts;
