    var regression_fx = []
    var regression_sales = []
    var regression_predicted_sales = []

    for (j=0;j<exchange_rate.length();j++){
        regression_fx.push(exchange_rate[i])
        regression_sales.push(myMap2.get(exchange_rate[i]))
        regression_predicted_sales.push(regression.transform([exchange_rate[i]]))
    }

