import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { 
  Card,
  ResourceList,
  Stack,
  TextStyle,
  Thumbnail, 
  Tabs
} from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';
//import Country_data from "../components/Country_data";


class ResourceListWithProducts extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      list: [],
      selected: 0,
      country_data: [] 
    }
    //console.log("1")
  }
  static contextType = Context;

  componentDidMount() {
    this.getList();
    //this.getCountryData('US')
    console.log("list2 is ", this.state.list)

  }

  getList = () => {
    //fetch('/api/getList')
    //fetch('https://ebb450c1.ngrok.io')
    fetch('http://localhost:5000/api/tabs')
    .then(res => res.json())
    .then(data => {
      console.log(data)
      this.setState(list, data[0])
      this.setState(list, data[1])
    })
    //console.log("res is ", res)
    //.then(list => this.setState({ list }))
    //.then(list => this.setState({ list}))
    //console.log("3")
  }


  getCountryData = () => {
    //fetch('/api/getList')
    //fetch('https://ebb450c1.ngrok.io')
    fetch('http://localhost:5000/api/country_data')
    .then(res => res.json())
    .then(country_data => this.setState({ country_data }))
    console.log("3")
  }


  render() {
    const { list, selected } = this.state;
    //console.log("List is ", list)
    const app = this.context;
    const tabs = list
    //console.log('ids are ',store.get('ids') )
    return (
      <Tabs tabs={tabs} selected={selected} onSelect={(selected) => this.setState({selected})} fitted={true} onClick={this.handleOnClick}>
      <p>in tab {selected}</p>


      </Tabs>
    );
  }
  handleOnClick() {
    console.log("here")
  }

}

 export default ResourceListWithProducts;
