}

function reformat_data(sales, exchange_rate){
    const data = []
    let myMap = Map()
    for (i=0;i<exchange_rate.length();i++){
        data.push([exchange_rate[i],sales[i]])
        myMap.set(exchange_rate[i],sales[i])
    }
    return [data, myMap]
}

module.exports = linear_regression;
