import React from 'react';
import {Card, Layout} from '@shopify/polaris';
import {HorizontalBar, Doughnut, Bar} from 'react-chartjs-2';
import store from 'store-js';
const getPearsonCorrelation = require('../functions/sales_exchange_rate_corr')

const date = store.get('date') 
const price = store.get('price') 
const exchange_rate = store.get('exchange_rate') 
const r = parseInt(Math.min(Math.abs(getPearsonCorrelation([34,37,24,67,42 ],[1.23,1.22, 1.23, 1.24, 1.25])), 1) * 100)
const doughnut = [r, 100-r]
console.log("date is ", date)
console.log("price is ", price)
console.log("exchange_rate is ", exchange_rate)
console.log("r is ", r)

var color = ''
if (r < 30){
    color = '#76b39d'
} else if (r < 40){
    color = '#F3DB26'
} else {
    color = '#FF5637'
}



const doughnutChartData = {
  labels: ["Moderate: "+r],
  datasets: [
    {
      //backgroundColor: ["#FF6D2D","#45853C"],
      backgroundColor: [color,"#EEEEEE"],
      borderColor: 'rgba(0, 0, 0, 0)',
      borderWidth: 2,
      data: doughnut,

    }
  ],
};

const doughnutChartOptions = {
  circumference: 1* Math.PI,
  rotation: Math.PI,
  maintainAspectRatio: true,
  cutoutPercentage:70,
  tooltips:{
    enabled:false,
  },
  legend: {
            display: true,
            position: 'bottom',
            align:'center',
            labels:{
                fontSize: 25,
                boxWidth: 0,
                fontColor: '#01023A'
            }
          },
  pieceLabel: {
    render: 'value' + '%',
    fontColor: "white",
  }
  };

var lineChartData = {
  //labels: ['04-19', '04-22', '04-25', '04-28', '05-01', '05-04', '05-07', '05-10', '05-13'],
  lables: date,
  datasets: [{
    type: "bar",
    label: "real",
    fill: false,
    lineTension: 0,
    backgroundColor: "rgb(1, 2, 58)",
    borderColor: "rgb(1, 2, 58)",
    borderCapStyle: 'butt',
    borderDash: [],
    borderDashOffset: 0.0,
    borderJoinStyle: 'miter',
    pointBorderColor: "rgb(1, 2, 58)",
    pointBackgroundColor: "rgb(1, 2, 58)",
    pointBorderWidth: 1,
    pointHoverRadius: 5,
    pointHoverBackgroundColor: "rgb(1, 2, 58)",
    pointHoverBorderColor: "rgb(1, 2, 58)",
    pointHoverBorderWidth: 2,
    pointRadius: 4,
    pointHitRadius: 10,
    // notice the gap in the data and the spanGaps: false
    //data: [1, 2, 3, 4, 3, 2, 4, 3, 1],
    data: price,
    spanGaps: false,
    },
    {
      type: "line",
      label: "real",
      fill: false,
      lineTension: 0,
      backgroundColor: "rgb(1, 2, 58)",
      borderColor: "rgb(1, 2, 58)",
      borderCapStyle: 'butt',
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: 'miter',
      pointBorderColor: "rgb(1, 2, 58)",
      pointBackgroundColor: "rgb(1, 2, 58)",
      pointBorderWidth: 1,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: "rgb(1, 2, 58)",
      pointHoverBorderColor: "rgb(1, 2, 58)",
      pointHoverBorderWidth: 2,
      pointRadius: 4,
      pointHitRadius: 10,
      // notice the gap in the data and the spanGaps: false
      //data: [7, 7.1005, 7.0906, 7.0879, 7.1372, 7.1542, 7.1257, 7.1673, 7.1562],
      data: exchange_rate,
      spanGaps: false,
      }

  ]
  };

var lineChartOptions = {
  maintainAspectRatio: true,
  tooltips:{
    enabled:true,
  },
  legend: {
    display: false,
  },
  scales: {
            yAxes: [{
                ticks: {
                    beginAtZero:false
                },
                scaleLabel: {
                      display: false,
                      labelString: 'USD/CNH',
                      fontSize: 20 
                  }
            }],
            xAxes: [{
              gridLines: {
                color: "rgba(0, 0, 0, 0)",
            }
            },],            
        },
        layout: {
          padding: {
              left: 0,
              right: 50,
              top: 0,
              bottom: 0
          }
      }   
};


const barChartData = {
  labels: ['China - Monthly CPI', 'US - Monthly CPI', 'China - Quarterly GDP',
            'US - Aaa Corporate Bonds', 'US - S&P 500'],
  datasets: [
    {
      backgroundColor: ["#ffa41b", '#005082','#ff5733','#708160','#00a8cc'],
      borderColor: 'rgba(0, 0, 0, 0)',
      borderWidth: 2,
      data: [0.65, 0.50, 0.47, 0.39, 0.37]
    }
  ],
};

const barChartOptions = {

  legend: {
            display: false,
          },

  scales: {
            xAxes: [{
              ticks: {
                min: 0,
                max: 1,
                callback: function(value) {
                    return value
                }
              },
              scaleLabel: {
                display: true,
                labelString: "Correlation Coefficient (Absolute Value)"
              }
            },],
            yAxes: [{
              barPercentage: 0.3,
              gridLines: {
                color: "rgba(0, 0, 0, 0)",
            }
            },],
          },
};


export default function Country_data() {  
    return (
      <Card>
            <Layout>
            <Layout.Section oneHalf>
                <Bar 
                  height="170vh"
                  data={lineChartData}
                  options={lineChartOptions}
                />
                <Doughnut
                    height="100vh"
                    data={doughnutChartData}
                    options={doughnutChartOptions}
                />

            </Layout.Section>
            <Layout.Section oneHalf>
                <HorizontalBar 
                    height="170vh"
                    data={barChartData}
                    options={barChartOptions}
                  />

            </Layout.Section>
          </Layout>
      </Card>
    );
  }
