import React, { useState }from 'react';
import {Card, Layout, DisplayText, TextStyle} from '@shopify/polaris';
import store from 'store-js';
import MapChart from "./MapChart";
import ReactTooltip from "react-tooltip";
import price_sensitive_market from "../functions/price_sensitive_market";



export default function Main_data(props) {  
    const [content, setContent] = useState("");
    const currency_list = price_sensitive_market(props.tabs, props.maps)

    console.log("currency_list is", currency_list)
    const price_sensitive_length = currency_list.length



    const data = {
      content: {
        body: [
          {
            _uid: "BUY6Drn9e1",
            component: "foo",
            headline: "Foo"
          },
          {
            _uid: "gJZoSLkfZV",
            component: "bar",
            title: "Bar"
          },
          {
            _uid: "X1JAfdsZxy",
            component: "foo",
            headline: "Another headline"
          }
        ]
      }
    };




    return (

          <Layout>


              <Layout.Section oneThird>

              <Card title="Price Range Analyzed" sectioned>
                  EUR: 2%<br/>
                  CAD: 5%<br/>
                  GBP: 10%<br/>
                </Card>
                </Layout.Section>
                <Layout.Section oneThird>
              <Card title="Your Most Price Sentitive Markets" sectioned>
                  {data.content.body.map(block => block.component)}
                  EUR: France, Italy, Germany<br/>
                  CAD: Countries: Canada<br/>
                  GBP: Countries: UK<br/>
                </Card>
                </Layout.Section>
                <Layout.Section oneThird>
                <Card title="Your Most Price Volatile Markets" sectioned>
                  EUR: France, Italy, Germany<br/>
                  CAD: Countries: Canada<br/>
                  GBP: Countries: UK<br/>
                </Card>

              </Layout.Section>
              <Layout.Section>
              <Card title="Price Change Magnitue the Past 30 Days" sectioned>

               <MapChart setTooltipContent={setContent} />
               <ReactTooltip>{content}</ReactTooltip>  
              </Card>
             </Layout.Section>

          </Layout>


    );
  }
