import React, { useState }from 'react';
import {Card, Layout, DisplayText, TextStyle, DataTable} from '@shopify/polaris';
import store from 'store-js';
import MapChart from "./MapChart";
import ReactTooltip from "react-tooltip";
import price_sensitive_market from "../functions/price_sensitive_market";



export default function Main_data(props) {  
    const [content, setContent] = useState("");
    const currency_list = price_sensitive_market(props.tabs, props.maps)

    console.log("currency_list is", currency_list)
    const price_sensitive_length = currency_list.length


    const rows = [
      ['Emerald Silk Gown', '$875.00', 124689, 140, '$122,500.00'],
      ['Mauve Cashmere Scarf', '$230.00', 124533, 83, '$19,090.00'],
      [
        'Navy Merino Wool Blazer with khaki chinos and yellow belt',
        '$445.00',
        124518,
        32,
        '$14,240.00',
      ],
    ];



    return (

          <Layout>


              <Layout.Section oneThird>

              <Card title="Price Range Analyzed" sectioned>
                  EUR: 2%<br/>
                  CAD: 5%<br/>
                  GBP: 10%<br/>
                </Card>
                </Layout.Section>
                <Layout.Section oneThird>
              <Card title="Your Most Price Sentitive Markets" sectioned>
              <DataTable
                  columnContentTypes={[
                    'text',
                    'numeric',
                    'numeric',
                    'numeric',
                    'numeric',
                  ]}
                  headings={[
                    'Product',
                    'Price',
                    'SKU Number',
                    'Net quantity',
                    'Net sales',
                  ]}
                  rows={rows}
                  totals={['', '', '', 255, '$155,830.00']}
                />

                  EUR: France, Italy, Germany<br/>
                  CAD: Countries: Canada<br/>
                  GBP: Countries: UK<br/>
                </Card>
                </Layout.Section>
                <Layout.Section oneThird>
                <Card title="Your Most Price Volatile Markets" sectioned>
                  EUR: France, Italy, Germany<br/>
                  CAD: Countries: Canada<br/>
                  GBP: Countries: UK<br/>
                </Card>

              </Layout.Section>
              <Layout.Section>
              <Card title="Price Change Magnitue the Past 30 Days" sectioned>

               <MapChart setTooltipContent={setContent} />
               <ReactTooltip>{content}</ReactTooltip>  
              </Card>
             </Layout.Section>

          </Layout>


    );

  }

  function displayBlock(currency) {
    render(
      {currency}
    )

  }
