import '../style/style.css'
import { AppProvider, Page, Card, DataTable } from '@shopify/polaris';
import '@shopify/polaris/styles.css';
import translations from '@shopify/polaris/locales/en.json';

const rows = [
  ['GBP', '1.23', 1.24, '10,000', '$122,500.00'],
  ['EUR', '1.10', 1.09, '5,000', '$19,090.00'],
  [
    'Navy Merino Wool Blazer with khaki chinos and yellow belt',
    '$445.00',
    124518,
    32,
    '$14,240.00',
  ],
];


class productsPrice extends React.Component {
    // Initialize the state
    constructor(props){
      super(props);
      this.state = {
        list: []
      }
      console.log("1")
    }
    // Fetch the list on first mount
    componentDidMount() {
      this.getList();
      console.log("2")
    }

    // Retrieves the list of items from the Express app
    getList = () => {
      //fetch('/api/getList')
      //fetch('https://ebb450c1.ngrok.io')
      fetch('http://localhost:5000/api/getList')
      .then(res => res.json())
      .then(list => this.setState({ list }))
      console.log("3")
    }  

  render() {
    const { list } = this.state;
    return (
      <div className="main_background_image">
      <h1>List of Items23</h1>
      {/* Check to see if any items are found*/}
      {list.length ? (
        <div>
          {/* Render the list of items */}
          {list.map((item) => {
            return(
              <div>
                {item}
              </div>
            );
          })}
        </div>
      ) : (
        <div>
          <h2>No List Items Found</h2>
        </div>
      )
    }

      <Page title="Gauranteed Exchange Rate">
            <Card>
              <DataTable
                columnContentTypes={[
                  'text',
                  'numeric',
                  'numeric',
                  'numeric',
                  'numeric',
                ]}
                headings={[
                  "Buyer's currency",
                  'Gauranteed Exchange Rate',
                  'Real-time Exchange Rate',
                  'Locked Amount',
                  'Extra Revenue',
                ]}
                rows={rows}
                totals={['', '', '', 255, '$155,830.00']}
              />
            </Card>
          </Page>



    </div>
    );
  }
}

 export default productsPrice;
